import React, {useState} from "react";

function App() {
    const [likes, setLikes] = useState(0)

    function incrementLikes() {
        setLikes(likes += 1)
    }

    function decrementLikes() {
        setLikes(likes -= 1)
    }

    return (
        <div className="App">
            <h1>{likes}</h1>
            <button onClick={incrementLikes}>Увеличить значение</button>
            <button onClick={decrementLikes}>Уменьшить значение</button>
        </div>
    );
}

export default App;
