import React, {useState} from "react";

function App() {
    const [likes, setLikes] = useState(0)
    const [value, setValue] = useState('INPUT TEXT')

    function incrementLikes() {
        setLikes(likes + 1)
    }

    function decrementLikes() {
        setLikes(likes - 1)
    }

    return (
        <div className="App">
            <h1>{likes}</h1>
            <h1>{value}</h1>
            <input
                type="text"
                value={value}
                onChange={event => setValue(event.target.value) }
            />
            <button onClick={incrementLikes}>Увеличить значение</button>
            <button onClick={decrementLikes}>Уменьшить значение</button>
            <input
                type="text"
                value={value}
                className={'ssss'}
            />
        </div>
    );
}

export default App;
